import 'package:flutter/material.dart';
import 'package:tts09b/List%20and%20Grid%20UI/reuseable_widgets.dart';

class ListViewScreen extends StatefulWidget {
  const ListViewScreen({super.key});

  @override
  State<ListViewScreen> createState() => _ListViewScreenState();
}

class _ListViewScreenState extends State<ListViewScreen> {

  List pName = ["Polo Shirt", "Rayban Glasses", "Addidas Shoes", "Rolex Watch", "Rado Watch"];
  List pCate = ["Shirt", "Glasses", "Shoes", "Watch", "Watch"];
  List pPrice = ["45.00", "23.56", "129.34", "500.00", "350.00"];
  List pAvail = ["Available","Available","Sold","Available","Sold"];
  List pImage = ["data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,"];

  @override
  Widget build(BuildContext context) {
    return  Scaffold(
      appBar: AppBar(
        title: Text("List Screen"),
        centerTitle: true,
      ),
      body: ListView.builder(
        itemCount: pName.length,
        itemBuilder: (context, index) {
        return ListContainer(
          productName: pName[index],
          productCate: pCate[index],
          productImage: pImage[index],
          productAvail: pAvail[index],
          productPrice: pPrice[index],
        );
      },),
    );
  }
}

